CREATE TABLE `classes` (`id` bigint NOT NULL AUTO_INCREMENT, `create_time` timestamp NULL, `update_time` timestamp NULL, `name` varchar(255) NOT NULL, `tuition` bigint NOT NULL, `start_date` timestamp NULL, `end_date` timestamp NULL, `instructor_classes` bigint NOT NULL, `schedule_classes` bigint NOT NULL, PRIMARY KEY (`id`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
CREATE TABLE `instructors` (`id` bigint NOT NULL AUTO_INCREMENT, `create_time` timestamp NULL, `update_time` timestamp NULL, `name` varchar(255) NOT NULL, `syllabic_characters` varchar(255) NOT NULL, `biography` varchar(255) NULL, `phone_number` varchar(255) NULL, `email` varchar(255) NULL, PRIMARY KEY (`id`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
CREATE TABLE `rooms` (`id` bigint NOT NULL AUTO_INCREMENT, `create_time` timestamp NULL, `update_time` timestamp NULL, `name` varchar(255) NOT NULL, `capacity` bigint NOT NULL, `studio_rooms` bigint NOT NULL, PRIMARY KEY (`id`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
CREATE TABLE `schedules` (`id` bigint NOT NULL AUTO_INCREMENT, `create_time` timestamp NULL, `update_time` timestamp NULL, `day_of_week` bigint NOT NULL, `start_time` varchar(255) NOT NULL, `end_time` varchar(255) NOT NULL, `room_schedules` bigint NOT NULL, `schedule_class` bigint NULL, PRIMARY KEY (`id`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
CREATE TABLE `schools` (`id` bigint NOT NULL AUTO_INCREMENT, `create_time` timestamp NULL, `update_time` timestamp NULL, `name` varchar(255) NOT NULL, PRIMARY KEY (`id`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
CREATE TABLE `studios` (`id` bigint NOT NULL AUTO_INCREMENT, `create_time` timestamp NULL, `update_time` timestamp NULL, `name` varchar(255) NOT NULL, `location` varchar(255) NOT NULL, `school_studios` bigint NOT NULL, PRIMARY KEY (`id`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
CREATE TABLE `users` (`id` bigint NOT NULL AUTO_INCREMENT, `create_time` timestamp NULL, `update_time` timestamp NULL, `name` varchar(255) NOT NULL, `firebase_uid` varchar(191) NOT NULL, PRIMARY KEY (`id`), UNIQUE INDEX `firebase_uid` (`firebase_uid`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
ALTER TABLE `classes` ADD CONSTRAINT `classes_instructors_classes` FOREIGN KEY (`instructor_classes`) REFERENCES `instructors` (`id`) ON DELETE NO ACTION, ADD CONSTRAINT `classes_schedules_classes` FOREIGN KEY (`schedule_classes`) REFERENCES `schedules` (`id`) ON DELETE NO ACTION;
ALTER TABLE `rooms` ADD CONSTRAINT `rooms_studios_rooms` FOREIGN KEY (`studio_rooms`) REFERENCES `studios` (`id`) ON DELETE NO ACTION;
ALTER TABLE `schedules` ADD CONSTRAINT `schedules_rooms_schedules` FOREIGN KEY (`room_schedules`) REFERENCES `rooms` (`id`) ON DELETE NO ACTION, ADD CONSTRAINT `schedules_classes_class` FOREIGN KEY (`schedule_class`) REFERENCES `classes` (`id`) ON DELETE SET NULL;
ALTER TABLE `studios` ADD CONSTRAINT `studios_schools_studios` FOREIGN KEY (`school_studios`) REFERENCES `schools` (`id`) ON DELETE NO ACTION;
